int num_oleds=0;
static unsigned char grayH;
static unsigned char grayL;

grove_oled grove_oled_init_pins(int sda, int scl) {
	for (int i = 0; i < num_oleds; ++i) {
		if (oled_devices[i].sda == sda && oled_devices[i].scl == scl) {
			return i;
		}
	}
	i2c device = i2c_open(sda, scl);
	return device;
/*	oled_devices[num_oleds].sda = sda;
	oled_devices[num_oleds].scl = scl;
	return num_oleds++;
*/
}

