void sendCommand(grove_oled device, u8 command){
   u8 buffer[2];
   buffer[0] = OLED_Command_Mode;
   buffer[1] = command;
   i2c_write(device, OLED_Address, buffer, 2);
}

void sendData(grove_oled device, u8 data){
   u8 buffer[2];
   buffer[0] = OLED_Data_Mode;
   buffer[1] = data;
   i2c_write(device, OLED_Address, buffer, 2);
}

void oled_init(grove_oled device){
    // Unlock OLED driver IC MCU interface
    sendCommand(device, 0xFD);
    sendCommand(device, 0x12);
    //Set display off
    sendCommand(device, 0xAE);
    // Switch on display
    sendCommand(device, 0xAF);
    // Set normal display
    sendCommand(device, 0xA4);
    // Init gray level for text. Default:Brightest White
    grayH= 0xF0;
    grayL= 0x0F;
}
