void grove_oled_setTextXY(grove_oled device, int Row, int Column){
    sendCommand(device, 0xB0 + Row);                   //set page address
    sendCommand(device, 0x00 + (8*Column & 0x0F));     //set column lower address
    sendCommand(device, 0x10 + ((8*Column>>4)&0x0F));  //set column higher address
}


void grove_oled_clearDisplay(grove_oled device){
    int i,j;
    sendCommand(device, OLED_Display_Off_Cmd);     //display off
    for(j=0;j<8;j++)
    {
        grove_oled_setTextXY(device, j,0);
        {
            for(i=0;i<16;i++)             //clear all columns
            {
                grove_oled_putChar(device,' ');
            }
        }
    }
    sendCommand(device, OLED_Display_On_Cmd);     //display on
    grove_oled_setTextXY(device, 0,0);
}
