void grove_oled_putChar(grove_oled device, unsigned char C){
    if(C < 32 || C > 127) //Ignore non-printable ASCII characters.
    {
        C=' '; //Space
    }
    
    int i;
    for(i=0;i<8;i++){
        sendData(device, BasicFont[C-32][i]);
    }
}

void grove_oled_putString(grove_oled device, const char *s){
    char ch;

    while(*s !=0) {
        ch = *s++;
        grove_oled_putChar(device, ch);
    }

}

