void grove_oled_activateScroll(grove_oled device){
    sendCommand(device, OLED_Activate_Scroll_Cmd);
}

void grove_oled_deactivateScroll(grove_oled device){
    sendCommand(device, OLED_Dectivate_Scroll_Cmd);
}

void grove_oled_setNormalDisplay(grove_oled device){
    sendCommand(device, OLED_Normal_Display_Cmd);
}

void grove_oled_setInverseDisplay(grove_oled device){
    sendCommand(device, OLED_Inverse_Display_Cmd);
}

void grove_oled_oled_initialize(grove_oled device) {
	oled_init(device);
	grove_oled_clearDisplay(device);
}
